	// convert to xml stream
	void CommBaseStateCore::to_xml(std::ostream &os, const std::string &indent) const {
		os << indent << "<timeStamp>";
		getTimeStamp().to_xml(os, indent);
		os << indent << "</timeStamp>";
		os << indent << "<basePose>";
		getBasePose().to_xml(os, indent);
		os << indent << "</basePose>";
		os << indent << "<baseOdomPose>";
		getBaseOdomPose().to_xml(os, indent);
		os << indent << "</baseOdomPose>";
		os << indent << "<baseVelocity>";
		getBaseVelocity().to_xml(os, indent);
		os << indent << "</baseVelocity>";
		os << indent << "<baseOdomVelocity>";
		getBaseOdomVelocity().to_xml(os, indent);
		os << indent << "</baseOdomVelocity>";
		os << indent << "<batteryState>";
		getBatteryState().to_xml(os, indent);
		os << indent << "</batteryState>";
	}
	
	// restore from xml stream
	void CommBaseStateCore::from_xml(std::istream &is) {
		static const Smart::KnuthMorrisPratt kmp_timeStamp("<timeStamp>");
		static const Smart::KnuthMorrisPratt kmp_basePose("<basePose>");
		static const Smart::KnuthMorrisPratt kmp_baseOdomPose("<baseOdomPose>");
		static const Smart::KnuthMorrisPratt kmp_baseVelocity("<baseVelocity>");
		static const Smart::KnuthMorrisPratt kmp_baseOdomVelocity("<baseOdomVelocity>");
		static const Smart::KnuthMorrisPratt kmp_batteryState("<batteryState>");
		
		if(kmp_timeStamp.search(is)) {
			CommBasicObjects::CommTimeStamp timeStampItem;
			timeStampItem.from_xml(is);
			setTimeStamp(timeStampItem);
		}
		if(kmp_basePose.search(is)) {
			CommBasicObjects::CommBasePose basePoseItem;
			basePoseItem.from_xml(is);
			setBasePose(basePoseItem);
		}
		if(kmp_baseOdomPose.search(is)) {
			CommBasicObjects::CommBasePose baseOdomPoseItem;
			baseOdomPoseItem.from_xml(is);
			setBaseOdomPose(baseOdomPoseItem);
		}
		if(kmp_baseVelocity.search(is)) {
			CommBasicObjects::CommBaseVelocity baseVelocityItem;
			baseVelocityItem.from_xml(is);
			setBaseVelocity(baseVelocityItem);
		}
		if(kmp_baseOdomVelocity.search(is)) {
			CommBasicObjects::CommBaseVelocity baseOdomVelocityItem;
			baseOdomVelocityItem.from_xml(is);
			setBaseOdomVelocity(baseOdomVelocityItem);
		}
		if(kmp_batteryState.search(is)) {
			CommBasicObjects::CommBatteryLevel batteryStateItem;
			batteryStateItem.from_xml(is);
			setBatteryState(batteryStateItem);
		}
	}
	
