	const char* CommBatteryEventCore::getCompiledHash()
	{
		return CommBasicObjectsIDL::REPO_HASH;
	}
	
	void CommBatteryEventCore::getAllHashValues(std::list<std::string> &hashes)
	{
		// get own hash value
		hashes.push_back(getCompiledHash());
	}
	
	void CommBatteryEventCore::checkAllHashValues(std::list<std::string> &hashes)
	{
		// check own hash value
		if (strcmp(getCompiledHash(), hashes.front().c_str()) != 0)
		{
			std::cerr << "###################################################" << std::endl;
			std::cerr << "WARNING: HASHES OF COMMUNICATION OBJECTS MISSMATCH!" << std::endl;
			std::cerr << "CommBatteryEventCore hash" << std::endl;
			std::cerr << "Expected: " << getCompiledHash() << std::endl;
			std::cerr << "Received: " << hashes.front() << std::endl;
			std::cerr << "###################################################" << std::endl;
		}
		assert(strcmp(getCompiledHash(), hashes.front().c_str()) == 0);
		hashes.pop_front();
		
	}
	
	#ifdef ENABLE_HASH
	size_t CommBatteryEventCore::generateDataHash(const DATATYPE &data)
	{
		size_t seed = 0;
		
		boost::hash_combine(seed, data.chargeValue);
		boost::hash_combine(seed, data.state);
		
		return seed;
	}
	#endif
	
	// default constructor
	CommBatteryEventCore::CommBatteryEventCore()
	:	idl_CommBatteryEvent()
	{  
		setChargeValue(0.0);
		setState(CommBasicObjects::ComparisonState());
	}
	
	CommBatteryEventCore::CommBatteryEventCore(const DATATYPE &data)
	:	idl_CommBatteryEvent(data)
	{  }

	CommBatteryEventCore::~CommBatteryEventCore()
	{  }
	
	void CommBatteryEventCore::to_ostream(std::ostream &os) const
	{
	  os << "CommBatteryEvent(";
	  os << getChargeValue() << " ";
	  getState().to_ostream(os);
	  os << ") ";
	}
	
	// convert to xml stream
	void CommBatteryEventCore::to_xml(std::ostream &os, const std::string &indent) const {
		os << indent << "<chargeValue>" << getChargeValue() << "</chargeValue>";
		os << indent << "<state>";
		getState().to_xml(os, indent);
		os << indent << "</state>";
	}
	
	// restore from xml stream
	void CommBatteryEventCore::from_xml(std::istream &is) {
		static const Smart::KnuthMorrisPratt kmp_chargeValue("<chargeValue>");
		static const Smart::KnuthMorrisPratt kmp_state("<state>");
		
		if(kmp_chargeValue.search(is)) {
			double chargeValueItem;
			is >> chargeValueItem;
			setChargeValue(chargeValueItem);
		}
		if(kmp_state.search(is)) {
			CommBasicObjects::ComparisonState stateItem;
			stateItem.from_xml(is);
			setState(stateItem);
		}
	}
	
