	void CommKBEventParamCore::to_ostream(std::ostream &os) const
	{
	  os << "CommKBEventParam(";
	  os << getQuery() << " ";
	  os << getLatestState() << " ";
	  os << getFormatingClause() << " ";
	  os << ") ";
	}
	
	// convert to xml stream
	void CommKBEventParamCore::to_xml(std::ostream &os, const std::string &indent) const {
		os << indent << "<query>" << getQuery() << "</query>";
		os << indent << "<latestState>" << getLatestState() << "</latestState>";
		os << indent << "<formatingClause>" << getFormatingClause() << "</formatingClause>";
	}
	
	// restore from xml stream
	void CommKBEventParamCore::from_xml(std::istream &is) {
		static const Smart::KnuthMorrisPratt kmp_query("<query>");
		static const Smart::KnuthMorrisPratt kmp_latestState("<latestState>");
		static const Smart::KnuthMorrisPratt kmp_formatingClause("<formatingClause>");
		
		if(kmp_query.search(is)) {
			std::string queryItem;
			is >> queryItem;
			setQuery(queryItem);
		}
		if(kmp_latestState.search(is)) {
			std::string latestStateItem;
			is >> latestStateItem;
			setLatestState(latestStateItem);
		}
		if(kmp_formatingClause.search(is)) {
			std::string formatingClauseItem;
			is >> formatingClauseItem;
			setFormatingClause(formatingClauseItem);
		}
	}
	
