	void CommKBEventResultCore::to_ostream(std::ostream &os) const
	{
	  os << "CommKBEventResult(";
	  os << getResult() << " ";
	  os << getFormatedResult() << " ";
	  os << ") ";
	}
	
	// convert to xml stream
	void CommKBEventResultCore::to_xml(std::ostream &os, const std::string &indent) const {
		os << indent << "<result>" << getResult() << "</result>";
		os << indent << "<formatedResult>" << getFormatedResult() << "</formatedResult>";
	}
	
	// restore from xml stream
	void CommKBEventResultCore::from_xml(std::istream &is) {
		static const Smart::KnuthMorrisPratt kmp_result("<result>");
		static const Smart::KnuthMorrisPratt kmp_formatedResult("<formatedResult>");
		
		if(kmp_result.search(is)) {
			std::string resultItem;
			is >> resultItem;
			setResult(resultItem);
		}
		if(kmp_formatedResult.search(is)) {
			std::string formatedResultItem;
			is >> formatedResultItem;
			setFormatedResult(formatedResultItem);
		}
	}
	
