	void CommMobileIRScanCore::checkAllHashValues(std::list<std::string> &hashes)
	{
		// check own hash value
		if (strcmp(getCompiledHash(), hashes.front().c_str()) != 0)
		{
			std::cerr << "###################################################" << std::endl;
			std::cerr << "WARNING: HASHES OF COMMUNICATION OBJECTS MISSMATCH!" << std::endl;
			std::cerr << "CommMobileIRScanCore hash" << std::endl;
			std::cerr << "Expected: " << getCompiledHash() << std::endl;
			std::cerr << "Received: " << hashes.front() << std::endl;
			std::cerr << "###################################################" << std::endl;
		}
		assert(strcmp(getCompiledHash(), hashes.front().c_str()) == 0);
		hashes.pop_front();
		
		// check hash value(s) for CommBasicObjects::CommBaseState(idl_CommMobileIRScan.baseState)
		CommBasicObjects::CommBaseState::checkAllHashValues(hashes);
		// check hash value(s) for CommBasicObjects::CommIRScan(idl_CommMobileIRScan.irScan)
		CommBasicObjects::CommIRScan::checkAllHashValues(hashes);
	}
	
	#ifdef ENABLE_HASH
	size_t CommMobileIRScanCore::generateDataHash(const DATATYPE &data)
	{
		size_t seed = 0;
		
		boost::hash_combine(seed, data.is_valid);
		seed += CommBasicObjects::CommBaseState::generateDataHash(data.baseState);
		seed += CommBasicObjects::CommIRScan::generateDataHash(data.irScan);
		
		return seed;
	}
	#endif
	
