	void CommMobileIRScanCore::to_ostream(std::ostream &os) const
	{
	  os << "CommMobileIRScan(";
	  os << getIs_valid() << " ";
	  getBaseState().to_ostream(os);
	  getIrScan().to_ostream(os);
	  os << ") ";
	}
	
	// convert to xml stream
	void CommMobileIRScanCore::to_xml(std::ostream &os, const std::string &indent) const {
		os << indent << "<is_valid>" << getIs_valid() << "</is_valid>";
		os << indent << "<baseState>";
		getBaseState().to_xml(os, indent);
		os << indent << "</baseState>";
		os << indent << "<irScan>";
		getIrScan().to_xml(os, indent);
		os << indent << "</irScan>";
	}
	
	// restore from xml stream
	void CommMobileIRScanCore::from_xml(std::istream &is) {
		static const Smart::KnuthMorrisPratt kmp_is_valid("<is_valid>");
		static const Smart::KnuthMorrisPratt kmp_baseState("<baseState>");
		static const Smart::KnuthMorrisPratt kmp_irScan("<irScan>");
		
		if(kmp_is_valid.search(is)) {
			bool is_validItem;
			is >> is_validItem;
			setIs_valid(is_validItem);
		}
		if(kmp_baseState.search(is)) {
			CommBasicObjects::CommBaseState baseStateItem;
			baseStateItem.from_xml(is);
			setBaseState(baseStateItem);
		}
		if(kmp_irScan.search(is)) {
			CommBasicObjects::CommIRScan irScanItem;
			irScanItem.from_xml(is);
			setIrScan(irScanItem);
		}
	}
	
