	const char* PropertySetEntryCore::getCompiledHash()
	{
		return CommBasicObjectsIDL::REPO_HASH;
	}
	
	void PropertySetEntryCore::getAllHashValues(std::list<std::string> &hashes)
	{
		// get own hash value
		hashes.push_back(getCompiledHash());
	}
	
	void PropertySetEntryCore::checkAllHashValues(std::list<std::string> &hashes)
	{
		// check own hash value
		if (strcmp(getCompiledHash(), hashes.front().c_str()) != 0)
		{
			std::cerr << "###################################################" << std::endl;
			std::cerr << "WARNING: HASHES OF COMMUNICATION OBJECTS MISSMATCH!" << std::endl;
			std::cerr << "PropertySetEntryCore hash" << std::endl;
			std::cerr << "Expected: " << getCompiledHash() << std::endl;
			std::cerr << "Received: " << hashes.front() << std::endl;
			std::cerr << "###################################################" << std::endl;
		}
		assert(strcmp(getCompiledHash(), hashes.front().c_str()) == 0);
		hashes.pop_front();
		
	}
	
	#ifdef ENABLE_HASH
	size_t PropertySetEntryCore::generateDataHash(const DATATYPE &data)
	{
		size_t seed = 0;
		
		boost::hash_combine(seed, std::string(data.key.c_str()));
		boost::hash_combine(seed, data.parent);
		
		return seed;
	}
	#endif
	
	// default constructor
	PropertySetEntryCore::PropertySetEntryCore()
	:	idl_PropertySetEntry()
	{  
		setKey("");
		setParent(0);
	}
	
	PropertySetEntryCore::PropertySetEntryCore(const DATATYPE &data)
	:	idl_PropertySetEntry(data)
	{  }

	PropertySetEntryCore::~PropertySetEntryCore()
	{  }
	
	void PropertySetEntryCore::to_ostream(std::ostream &os) const
	{
	  os << "PropertySetEntry(";
	  os << getKey() << " ";
	  os << getParent() << " ";
	  os << ") ";
	}
	
	// convert to xml stream
	void PropertySetEntryCore::to_xml(std::ostream &os, const std::string &indent) const {
		os << indent << "<key>" << getKey() << "</key>";
		os << indent << "<parent>" << getParent() << "</parent>";
	}
	
	// restore from xml stream
	void PropertySetEntryCore::from_xml(std::istream &is) {
		static const Smart::KnuthMorrisPratt kmp_key("<key>");
		static const Smart::KnuthMorrisPratt kmp_parent("<parent>");
		
		if(kmp_key.search(is)) {
			std::string keyItem;
			is >> keyItem;
			setKey(keyItem);
		}
		if(kmp_parent.search(is)) {
			unsigned int parentItem;
			is >> parentItem;
			setParent(parentItem);
		}
	}
	
