void CommPropertySet::toIdlType()
{
  const unsigned int num_sets = this->num_sets();
  idl_CommPropertySet.sets.resize(num_sets);
  const unsigned int num_items = this->num_items();
  idl_CommPropertySet.items.resize(num_items);

  std::map<std::string,Property*>::const_iterator iter;
  unsigned int set_index = 0;
  unsigned int item_index = 0;
  std::list<std::pair<const PropertySet*,unsigned int> > stack;

  idl_CommPropertySet.sets[set_index].key = "root";
  idl_CommPropertySet.sets[set_index].parent = 0;
  stack.push_front(std::make_pair(this, set_index));
  ++set_index;

  while(!stack.empty())
  {
    const PropertySet *prop_set = stack.front().first;
    const unsigned int parent = stack.front().second;
    stack.pop_front();

    iter = this->_get_children(prop_set)->begin();
    while(iter != this->_get_children(prop_set)->end())
    {
      const Smart::PropertyItem *leaf = dynamic_cast<const Smart::PropertyItem*>((*iter).second);
      if(leaf)
      {
        idl_CommPropertySet.items[item_index].key = (*iter).first.c_str();
        idl_CommPropertySet.items[item_index].value = leaf->getString().c_str();
        idl_CommPropertySet.items[item_index].parent = parent;
        ++item_index;
      }
      else
      {
        const PropertySet *subset = dynamic_cast<const PropertySet*>((*iter).second);
        idl_CommPropertySet.sets[set_index].key = (*iter).first.c_str();
        idl_CommPropertySet.sets[set_index].parent = parent;
        stack.push_front(std::make_pair(subset,set_index));
        ++set_index;
      }
      ++iter;
    }
  }
}

